import { languages } from './languages.js';
import React from 'react'

export default function Main(){

const [LanguagesObjectArray, setLanguages] = React.useState(languages)
const [CurrentWord, setCurrentWord] = React.useState("react")
const [GuessedArray, setGuessed] = React.useState([])
const [CorrectLetters, setCorrectLetters] = React.useState([])


const SplitCurrentWord = CurrentWord.split("");
const AlphabetArray = "abcdefghijklmnopqrstuvwxyz".split("")

console.log(GuessedArray)

function HandleLetterButtonClick(Letter){
    setGuessed(prevGuessed => {
        const newGuessed = prevGuessed.includes(Letter) ? prevGuessed : [...prevGuessed, Letter]
        CompareGuessed(newGuessed);
        return newGuessed;
    });
}

function CompareGuessed(updatedGuessedArray){
    setCorrectLetters(prevCorr => {
        const newCorrectLetters = SplitCurrentWord.filter(letter => updatedGuessedArray.includes(letter));
        return [...new Set([...prevCorr, ...newCorrectLetters])];
    });
}



const WordDisplay = SplitCurrentWord.map(prevWord => {
    return <span className='Letters'>{prevWord.toUpperCase()}</span>
})

const AlphabetButtons = AlphabetArray.map(preAlph => {

    if(CorrectLetters.includes(preAlph)){
        return <button style={{backgroundColor: "green"}} onClick={() => HandleLetterButtonClick(preAlph)} className='AlphabetLetters'>{preAlph.toUpperCase()}</button>
    }
    else if(!CorrectLetters.includes(preAlph) && GuessedArray.includes(preAlph)){
        return <button style={{backgroundColor: "red"}} onClick={() => HandleLetterButtonClick(preAlph)} className='AlphabetLetters'>{preAlph.toUpperCase()}</button>
    }
    else {
        return<button onClick={() => HandleLetterButtonClick(preAlph)} className='AlphabetLetters'>{preAlph.toUpperCase()}</button>
    }
})

const LanguagesDisplay = LanguagesObjectArray.map(prevLang => {
    return <h2 className="Languages" key={prevLang.name} style={{backgroundColor: prevLang.backgroundColor, color: prevLang.color}}>{prevLang.name}</h2>
})

    return(
        <main>
            <div className="GameResultStatus">
                <h2>You Win!</h2>
                <p>ðŸŽ‰CongratulationsðŸŽ‰</p>
            </div>
            <div className="LanguagesCollection">
                {LanguagesDisplay}
            </div>
            <div className='WordToGuess'>
                {WordDisplay}
            </div>
            <div className='AlphabetButtons'>
                {AlphabetButtons}
            </div>
            <button className='NewGameButton'>New Game</button>
        </main>
    )
}
